import React, {useEffect, useState} from 'react';
import {Link} from 'react-router-dom';

import {FiArrowRight, FiPlus} from  'react-icons/fi';
import {Map, TileLayer, Marker, Popup} from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import leaflet from 'leaflet'

import api from '../services/api'

import mapMarkerImg from '../images/map_marker.svg'
import '../styles/pages/orphanages_map.css'

interface Orphanage{
    id: number,
    latitude: number,
    longitude: number,
    name: string
}

const mapIcon = leaflet.icon({
    iconUrl: mapMarkerImg,
    iconAnchor: [29, 68],
    iconSize: [58, 68],

    popupAnchor: [170, 2]
})

function OrphanagesMap(){
    const [orphanages, setOrphanages] = useState<Orphanage[]>([])

    useEffect(()=>{
        api.get('/orphanages').then(response => {
            setOrphanages(response.data)})
    }, [])

    console.log(orphanages)

    return (
        <div className="page-map">
            <aside>
                <header>
                    <img src={mapMarkerImg} alt=""/>
                    <p>Muitas crianças estão esperando sua visita :)</p>
                </header>

                <footer>
                    <strong>Florianopólis</strong>
                    <span>Santa Catarina</span>
                </footer>
            </aside>

            <Map
            center={[-27.6987904,-48.5011278]}
            zoom={13}
            style={{width: '100%', height:'100%'}}
            >
                <TileLayer url='https://a.tile.openstreetmap.org/{z}/{x}/{y}.png'/>
                {orphanages.map(orphanage => {
                            <Marker position={[orphanage.latitude, orphanage.longitude]} icon={mapIcon}>
                                <Popup closeButton={false} minWidth={240} maxWidth={240} className="mapPopup">
                                    orphanage.name
                                    <Link to="orphanages/1">
                                        <FiArrowRight size={20} color="#FFF"/>
                                    </Link>
                                </Popup>
                            </Marker>
                })}
            </Map>

            <Link to='/orphanages/create' className="create-orphanage">
                <FiPlus size={32} color="#FFF"></FiPlus>
            </Link>
        </div>
    );
}

export default OrphanagesMap;
